import React, { useContext, useState } from "react";
import { getAuth, signInWithEmailAndPassword } from "firebase/auth";
import { useNavigate } from "react-router-dom";
import { app } from "../firebase";
import { Button, TextField } from "@mui/material";
import backgroundImage from "../bg.jpeg";
import { ToastContainer, toast, Bounce } from "react-toastify";
import { AuthContext } from "../Context/AuthContext";
import { ref, push, onValue, getDatabase, set } from "firebase/database";

const Login = (props) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();
  const authContext = useContext(AuthContext);
  const database = getDatabase(app);

  const notify = (message) =>
    toast(message, {
      position: "top-right",
      autoClose: 5000,
      closeOnClick: true,
      pauseOnHover: true,
      theme: "dark",
    });

  const handleSubmit = async (e) => {
    e.preventDefault();
    const auth = getAuth(app);
    signInWithEmailAndPassword(auth, email, password)
      .then((userCredentials) => {
        console.log("Logged IN ", userCredentials.user.uid);
        const loginRef = ref(database, `userData/${userCredentials.user.uid}`);
        onValue(loginRef, (snapshot) => {
          const messagesData = snapshot.val();
          if (messagesData) {
            console.log("THE MESSAGES DATA ", messagesData);
            authContext.setUserInformation(messagesData);
          } else {
            console.log("ERROR ");
          }
        });
        notify("Logged In Successfully");
        navigate("/dashboard");
      })
      .catch((e) => {
        notify("Error " + e.message);
        console.log("ERROR");
      });
  };
  return (
    <div
      className="login-main-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      <div className="login-form-container">
        <div className="login-form-heading">Login to Continue</div>

        <div style={{ display: "flex", gap: "20px" }}>
          <TextField
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Enter Email"
            label="Email"
          />
          <TextField
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Enter Password"
            label="Password"
          />
        </div>

        <div className="homeButton" type="submit" onClick={handleSubmit}>
          Login
        </div>
        <div>or</div>
        <div className="auth-page-switcher" onClick={() => navigate("/signup")}>
          New User? Sign Up
        </div>
      </div>
    </div>
  );
};

export default Login;

