import React, { useState } from "react";
import { getAuth, createUserWithEmailAndPassword } from "firebase/auth";
import { useNavigate } from "react-router-dom";
import { app } from "../firebase";
import { Button, TextField } from "@mui/material";
import backgroundImage from "../bg.jpeg";
import { ToastContainer, toast, Bounce } from "react-toastify";
import { ref, push, onValue, getDatabase, setV } from "firebase/database";

const SignUp = (props) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [name, setName] = useState("");
  const [gender, setGender] = useState("");
  const [phone, setPhone] = useState("");
  const [age, setAge] = useState("");
  const navigate = useNavigate();
  const database = getDatabase(app);
  const notify = (message) =>
    toast(message, {
      position: "top-right",
      autoClose: 5000,
      closeOnClick: true,
      pauseOnHover: true,
      theme: "dark",
    });

  const handleSubmit = async (e) => {
    e.preventDefault();
    const auth = getAuth(app);
    createUserWithEmailAndPassword(auth, email, password)
      .then(() => {
        console.log("User created successfully");
        const signUpRef = ref(database, `userData/${phone}`);
        console.log("The signup ref ", signUpRef);
        const firebaseData = {
          email,
          password,
          name,
          gender,
          phone,
          age,
        };
        push(signUpRef, firebaseData);
        notify("Signed Up Successfully");

        navigate("/dashboard");
      })
      .catch((error) => {
        notify("ERROR " + error.message);
        const errorCode = error.code;
        const errorMessage = error.message;
        console.error("Error creating user:", errorCode, errorMessage);
      });
  };

  return (
    <div
      className="login-main-container"
      style={{ backgroundImage: `url(${backgroundImage})` }}
    >
      <div className="login-form-container">
        <div className="login-form-heading">Sign Up</div>

        <div style={{ display: "flex", gap: "20px" }}>
          <TextField
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            placeholder="Enter Email"
            label="Email"
          />
          <TextField
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            placeholder="Enter Password"
            label="Password"
          />
        </div>
        <div style={{ display: "flex", gap: "20px" }}>
          <TextField
            type="name"
            value={name}
            onChange={(e) => setName(e.target.value)}
            placeholder="Enter Name"
            label="Name"
          />
          <TextField
            type="phone"
            value={phone}
            onChange={(e) => setPhone(e.target.value)}
            placeholder="Enter Phonenumber"
            label="Phonenumber"
          />
        </div>
        <div style={{ display: "flex", gap: "20px" }}>
          <TextField
            type="gender"
            value={gender}
            onChange={(e) => setGender(e.target.value)}
            placeholder="Enter Gender"
            label="Gender"
          />
          <TextField
            type="age"
            value={password}
            onChange={(e) => setAge(e.target.value)}
            placeholder="Enter Age"
            label="Age"
          />
        </div>

        <div className="homeButton" onClick={handleSubmit}>
          Sign Up
        </div>

        <div>or</div>
        <div className="auth-page-switcher" onClick={() => navigate("/login")}>
          Already have an account? Login
        </div>
      </div>
    </div>
  );
};

export default SignUp;
